print('<meta charset="utf8">');


function print (msg) { 
	console.log(msg); 
}

function error (msg) { 
	console.log('<div style="display:block; padding-top:100px; text-align:center; color:red; font-size:30px; font-weight:bold;">' + msg + '</div>'); 
}


var _get = {};
(process.argv[2] || '').replace( /[A-Z0-9]+?=([^\b|&]*)/gi, function(a) {
	_get[a.split('=').shift()] = decodeURIComponent(a.split('=').pop());
});

exports.error 	= error;
exports.print 	= print;
exports.get 	= _get;