"use strict";
var ele = document.querySelector('h1'), flag, sid,
	c = function () { return Math.round(Math.random()*1000%256) };

ele && ele.addEventListener('click', function (e) {
	(flag = !flag)
		? (sid = setInterval(function () { ele.style.color = 'rgba('+[c(),c(),c(), Math.min(1, 1.5*Math.random())].join(',')+')'; }, 160))
		: clearInterval(sid);
	//ele.removeEventListener('click', arguments.callee, false);
}, false);
